export default class TailwindAlert {
  static show({ title = "Alert", message = "", type = "success", onConfirm = null, onCancel = null }) {
    const existingAlert = document.getElementById("tailwind-alert");
    if (existingAlert) existingAlert.remove();

    const images = {
      warning:"data:image/png;base64,",
      rejected: "data:image/png;base64,", // Replace with your rejected image
      success: "data:image/png;base64,", // Replace with your success image
    };

    const overlay = document.createElement("div");
    overlay.id = "tailwind-alert";
    overlay.className = "tailwind-alert-overlay";

    const alertBox = document.createElement("div");
    alertBox.className = `tailwind-alert-box tailwind-alert`;

    const imageElem = document.createElement("img");
    imageElem.src = images[type] || images.warning;
    imageElem.alt = type;
    imageElem.className = "w-16 h-16 mx-auto mb-4";

    const titleElem = document.createElement("h2");
    titleElem.className = "tailwind-alert-title";
    titleElem.innerText = title;

    const messageElem = document.createElement("p");
    messageElem.className = "tailwind-alert-message";
    messageElem.innerText = message;

    const btnContainer = document.createElement("div");
    btnContainer.className = "tailwind-alert-buttons";



    const confirmBtn = document.createElement("button");
    confirmBtn.className = `tailwind-alert-confirm ${
        type === "error" ? "tailwind-alert-confirm-error" :
            type === "warning" ? "tailwind-alert-confirm-error" :
                type === "success" ? "tailwind-alert-confirm-success" :
                    "tailwind-alert-confirm-info"
    }`;
    confirmBtn.innerText = "OK";
    confirmBtn.onclick = () => {
      if (onConfirm) onConfirm();
      overlay.remove();
      document.removeEventListener('keydown', handleEnterPress);
    };
    const handleEnterPress = (event) => {
      if (event.key === 'Enter') {
        confirmBtn.click();  // Trigger click event programmatically
      }
    };
    document.addEventListener('keydown', handleEnterPress);
    let cancelBtn = null;
    if (onCancel) {
      cancelBtn = document.createElement("button");
      cancelBtn.className = `tailwind-alert-cancel`;
      cancelBtn.innerText = "Cancel";
      cancelBtn.onclick = () => {
        onCancel();
        overlay.remove();
      };
      btnContainer.appendChild(cancelBtn);
    }

    btnContainer.appendChild(confirmBtn);
    alertBox.append(imageElem, titleElem, messageElem, btnContainer);
    overlay.appendChild(alertBox);
    document.body.appendChild(overlay);

    setTimeout(() => alertBox.classList.remove("scale-95"), 10);
  }
}
